import React, { Component } from 'react';
import { ExtColorselector } from "@sencha/ext-react-modern";

export default class App extends Component {
  onCancel = ({sender}) => {
    console.log('Cancel')
  };

  render() {
    return (
      &lt;ExtColorselector onCancel={this.onCancel}&gt;&lt;/ExtColorselector&gt;
    )
  }
}
</pre>

